mapper.select:
SELECT id, category, title, url, file_id AS fileId, thumbnail_id AS thumbnailId, description, hashtags, user_id AS userId, create_dt AS createDt, mod_dt AS modDT, delete_yn AS deleteYn 
FROM board where id = #{id} 
WHERE delete_yn = '0'

mapper.update:
UPDATE board
SET delete_yn = '1'
WHERE id=#{id}
